import React, {Component, Fragment} from 'react';
import { SketchPicker } from 'react-color';

import './BoardBuilder.css';

import Button from '@material-ui/core/Button';
import Board from '../../components/Board/Board';
import Frames from '../../components/Board/Controllers/Frames';
import ArrowBackIcon from '@material-ui/icons/ArrowBack';
import ArrowForwardIcon from '@material-ui/icons/ArrowForward';

const BASICS = ['#FF0000', '#FF8000', '#FFFF00', '#00FF00', '#00FFFF', '#7F00FF', '#FFFFFF', "#000000"];
const WIDTH = 15;
const HEIGHT = 15;

class BoardBuilder extends Component {

    constructor(props) {
        super(props);
        this.state = {
            boardWidth: WIDTH,
            boardHeight: HEIGHT,
            animation: [],
            currentFrame: [],
            frame: 0,
            currentColor: "#000000",
            history: [],
            sketchColor: "#FFFFFF"
        }

        let frame = new Array(this.state.boardWidth * this.state.boardHeight);
        frame.fill("#000000");
        this.state.animation.push(frame);
        this.state.currentFrame = frame;
    }

    palletColorHandler = (color) => {
        this.setState({currentColor: color})
        console.log(color);
    }

    pixelColorHandler = (index) => {
        let tempFrame = [...this.state.currentFrame];
        tempFrame[index] = this.state.currentColor;
        this.setState({currentFrame: tempFrame});
    }

    handleChange = (color) => {
        this.setState({ currentColor: color.hex });
        this.setState({ sketchColor: color.hex });
      };

    arrowHandler = (direction) => {
        console.log(direction);
    }

    render(){
        return(
            <Fragment>
                <div className={"boardBuilder__container"}>
                    <div className={"boardBuilder__colors"}>
                        <div>
                            <span>Current Color</span>
                            <div className="boardBuilder__color" />
                        </div>
                        <div>
                            <span>Sketch Color</span>
                            <div className="boardBuilder__color">color</div>
                            <Button>Select</Button>
                        </div>
                        <SketchPicker
                            color={this.state.sketchColor}
                            disableAlpha="true"
                            presetColors={BASICS}
                            onChange={this.handleChange}/>
                    </div>
                    <Board 
                        width={this.state.boardWidth}
                        height={this.state.boardHeight}
                        frame={this.state.currentFrame}
                        pixelClicked={this.pixelColorHandler} />
                </div>
                <div>
                    <ArrowBackIcon onClick={() => this.arrowHandler("back")}/>
                    <Frames />
                    <ArrowForwardIcon onClick={() => this.arrowHandler("forward")}/>
                </div>
            </Fragment>
        );
    };
}

export default BoardBuilder;
