import React, {Component} from 'react';
import './BoardBuilder.css';
import { SketchPicker } from 'react-color';

import Board from '../../components/Board/Board';
import Pallet from '../../components/Board/Pallet/Pallet';

class BoardBuilder extends Component {

    constructor(props) {
        super(props);
        this.state = {
            boardWidth: 15,
            boardHeight: 15,
            animation: [],
            frame: 0,
            history: [],
                    // white, black, red, green, blue, yellow, orange
            basics: ["#ffffff", "#000000", "#ff0000", "#00ff00", "#0000ff", "#ffff00", "#ffa500"]
        }

        let frame = new Array(this.state.boardWidth * this.state.boardHeight);
        frame.fill("#000000");
        this.state.animation.push(frame);
    }
    render(){
        return(
            <div className={"boardBuilder__container"}>
                <Board 
                    width={this.state.boardWidth}
                    height={this.state.boardHeight}
                    frame={this.state.animation[this.state.frame]}/>
                <Pallet 
                    title="Basic Colors" 
                    colors={this.state.basics} />
                <SketchPicker />
            </div>
        );
    };
}

export default BoardBuilder;
