import React from 'react';
import './Board.css'

import Aux from '../../hoc/Aux';

const Board = (props) => {

    let pixels = props.frame.map((pixel, index) => {

        let temp = <div
            key={"pixel" + index}
            style={{backgroundColor: pixel}}
            className={"board__pixel"}
            >{pixel}</div>

        if(index%props.width == 0)
            return <Aux><br/>{temp}</Aux>
        else
            return {temp}
    })

    return (
        <div className={"board__container"}>
            {pixels}
        </div>
    );
}

export default Board;
