import React from 'react';

import AddBoxIcon from '@material-ui/icons/AddBox';
import ArrowBackIcon from '@material-ui/icons/ArrowUp';
import ArrowForwardIcon from '@material-ui/icons/ArrowDown';
import Menu from '@material-ui/core/Menu';
import MenuItem from '@material-ui/core/MenuItem';

export default function Frames() {
    const [anchorEl, setAnchorEl] = React.useState(null);

    const handleClick = (event) => {
        setAnchorEl(event.currentTarget);
    };

    const handleClose = () => {
        setAnchorEl(null);
    };

    return(
        <div>
            <AddBoxIcon 
                aria-controls="simple-menu"
                aria-haspopup="true"
                onClick={handleClick}
                fontSize="large"
                style={{ color: "lime" }} />
            <Menu
                id="simple-menu"
                anchorEl={anchorEl}
                keepMounted
                open={Boolean(anchorEl)}
                onClose={handleClose}
            >
                <MenuItem onClick={handleClose}>New</MenuItem>
                <MenuItem onClick={handleClose}>Copy</MenuItem>
            </Menu>
            <ArrowBackIcon onClick={() => this.arrowHandler("back")}/>
            <ArrowForwardIcon onClick={() => this.arrowHandler("forward")}/>
        </div>
    )
};
