import React from 'react';
import './Board.css'

import Aux from '../../hoc/Aux';

const Board = (props) => {

    let pixels = [];

    for(let h=0; h<props.height; h++){
        let row = [];

        for(let w=0; w<props.width; w++){
            let num;

            if(h%2 == 0)
                num = h * w + w;
            else 
                num = h * w - w + w +1;

            row.push(
                <div
                    key={"pixel" + num}
                    style={{backgroundColor: props.frame[num]}}
                    className={"board__pixel"}
                >pixel {num}</div>
            )
        }

        pixels.push(<div className="board__row">{row}</div>) 
    }

    return pixels;
}

export default Board;
